var mock = require('mockjs')
import qs from 'qs'
/* 
 * 学校http请求模块
 */
const pschoolData={
    totalRows:10,
    results:[]
}

function RandomData(pageSize){
    var size=pschoolData.totalRows;
    if(pageSize){
        size=pageSize;
    }
    var results=[];
    for(var i=0;i<size;i++){
        var item={ "uuid": mock.Random.guid(), "name": mock.Random.cword(3,5),"address": mock.Random.cword(5,15)}
        results.push(item);
    }
    return {
        'totalRows':pschoolData.totalRows,
        'results':results
    };
}

// 添加
export const addPschool = (data) => {
    return {
        url: '/rest/pschool/addPschool',
        method: 'post',
        data:data,
        openMock:true,
        mock:function(params){
            var qparams=JSON.parse(params);
            pschoolData.results.push(qparams);
            pschoolData.totalRows++;
            qparams['uuid']=mock.Random.guid();
            return qparams
        }
    }
}
//修改
export const updPschool = (data) => {
    return {
        url: '/rest/pschool/updPschool',
        method: 'post',
        data:data,
        openMock:true,
        mock:function(params){
            var qparams=JSON.parse(params);
            return qparams
        }
    }
}
// 删除
export const delPschool = (data) => {
    return {
        url: '/rest/pschool/delPschool',
        method: 'post',
        data:data,
        openMock:true,
        mock:function(params){
            pschoolData.totalRows--;
            return params
        }
    }
}
// 分页查询
export const dirPschool = (data) => {
    return {
        url: '/rest/pschool/dirPschool',
        method: 'post',
        data:data,
        openMock:true,
        mock:function(params){
            var qparams=JSON.parse(params);
            var rData=RandomData(qparams.page.pageSize);
            return { 
                "results": rData.results,
                "totalRows": rData.totalRows
            }
        }
    }
}
//查看
export const getPschool = (data) => {
    return {
        url: '/rest/pschool/getPschool',
        method: 'get',
        data:data,
        openMock:false,
        mock:function(params){ 
            return null;
        }
    }
}
