var mock = require('mockjs')

/* 
 * 上传
 */
// 上传文件
export const uploadFile = (url,data,callback) => {
    return {
        url: url,
        method: 'post',
         headers:{
            'Content-Type':'multipart/form-data' 
        }, 
        data:data,
        openMock:true,
        mock:function(param){
            return {"uuid":"97518dd791be4bba9a219d498974f02f","userId":"48c19d1d6d474896b9119e19d4c4e312","fileUrlFull":"http://127.0.0.1:8088/0/9c1c2ac6-f44e-4263-9731-ff4932c0b50d.jpg","fileName":"333","fileExt":"jpg","fileSize":159988,"time":"2020-05-28 16:44:03"}
        }
    }
}

//获取文件信息
export const getFilesMsg = (url,data) =>{
    return {
        url:'rest/attachment/loadAttachment',
        method:'post',
        data:data,
        openMock:true,
        mock:function(params){
            return {
                
                    "config": {
                        "uuid": "196e6be5159f7a63cbe745b8ab9b79f6",
                        "name": "世界风景名胜图片",
                        "orderNum": 1,
                        "fileLength": 50000000,
                        "fileNumber": 1,
                        "fileExt": "*",
                        "extraPara": ""
                    },
                    "list":[{
                        "uuid": "afc5a6a4ac176c763ed5242992506fc6",
                        "tableUuid": "pic002",
                        "fileName": "b453ce9dba864f89",
                        "fileExt": "jpg",
                        "fileSize": 14456,
                        "userId": "001",
                        "time": "2020-05-12 09:21:37",
                        "orderNum": 1,
                        "fileUrlFull": "http://218.247.237.33:9132/0/30fb7b7c-9617-43ef-91eb-a3c7dda0550e",
                        "category": "pic"
                    }]
                
              }
        }
    }
}

//修改文件名称
export const changeFileName = (url,data) => {
    return {
        url:url,
        method:'post',
        data:data,
        openMock:true,
        mock:function (param) { 
            return {"uuid":"afc5a6a4ac176c763ed5242992506fc6","fileName":param}
         }
    }
}

//删除文件
export const deleteFile = (url,data) => {
    return {
        url:url,
        method:'post',
        data:data,
        openMock:true,
        mock:function (param) {
            return []
        }
    }
}

//下载文件
export const downloadAttachment = (url,data) => {
    return {
        url:url,
        method:'get',
        data:data,
        responseType: 'blob',
        openMock:true,
        mock:function(param){
            return null;
        }
    }
}
